import React from "react";
import '../../styles/About/AboutTeam.css';

const team = [
  {
    name: "Nguyễn Văn A",
    role: "Trưởng dự án",
    img: "/assets/images/avatar-1.jpg"
  },
  {
    name: "Trần Thị B",
    role: "Lập trình viên Frontend",
    img: "/assets/images/avatar-2.jpg"
  },
  {
    name: "Lê Văn C",
    role: "Lập trình viên Backend",
    img: "/assets/images/avatar-3.jpg"
  }
];

const AboutTeam = () => (
  <section className="about-team py-5">
    <div className="container">
      <h2 className="text-center fw-bold mb-5">Đội ngũ phát triển</h2>
      <div className="row justify-content-center g-4">
        {team.map((member, idx) => (
          <div className="col-md-4" key={idx}>
            <div className="team-card text-center p-4 bg-white rounded shadow-sm h-100">
              <img
                src={member.img}
                alt={member.name}
                className="team-avatar mb-3 rounded-circle shadow"
                width={120}
                height={120}
              />
              <h5 className="fw-bold mb-1">{member.name}</h5>
              <p className="text-muted mb-0">{member.role}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  </section>
);

export default AboutTeam;
