import React, { useState, useEffect } from 'react';
import ReviewList from './ReviewSection/ReviewList';
import ReviewForm from './ReviewSection/ReviewForm';
import '../../styles/itineraryCSS/ItineraryDetail.css';

const ItineraryDetail = ({ tourId }) => {
  const [reviews, setReviews] = useState([]);
  const [showReviewForm, setShowReviewForm] = useState(false);

  useEffect(() => {
    fetchReviews();
  }, [fetchReviews]);

  const fetchReviews = async () => {
    try {
      const response = await fetch(`/api/reviews/tour/${tourId}`);
      const data = await response.json();
      if (data.success) {
        setReviews(data.data);
      }
    } catch (error) {
      console.error('Lỗi khi tải đánh giá:', error);
    }
  };

  const handleSubmitReview = async (reviewData) => {
    try {
      const formData = new FormData();
      formData.append('rating', reviewData.rating);
      formData.append('comment', reviewData.comment);
      reviewData.images.forEach(image => {
        formData.append('images', image);
      });

      const response = await fetch(`/api/reviews/${tourId}`, {
        method: 'POST',
        body: formData,
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        fetchReviews();
        setShowReviewForm(false);
      }
    } catch (error) {
      console.error('Lỗi khi gửi đánh giá:', error);
    }
  };

  return (
    <div className="itinerary-detail">
      <section className="reviews-section">
        <div className="reviews-header">
          <h2>Đánh giá từ khách hàng</h2>
          <button 
            onClick={() => setShowReviewForm(true)}
            className="add-review-btn"
          >
            Viết đánh giá
          </button>
        </div>

        {showReviewForm && (
          <ReviewForm 
            onSubmit={handleSubmitReview}
            onCancel={() => setShowReviewForm(false)}
          />
        )}

        <ReviewList reviews={reviews} />
      </section>
    </div>
  );
};

export default ItineraryDetail;
