import React from "react";
import '../../../styles/About/AboutBanner.css'; 

const AboutBanner = () => (
  <section className="about-banner py-5 bg-primary text-white text-center">
    <div className="container">
      <h1 className="display-4 fw-bold">Về Phú Yên Travel</h1>
      <p className="lead mt-3">
        Khám phá, trải nghiệm và kết nối – Nơi hành trình của bạn bắt đầu!
      </p>
    </div>
  </section>
);

export default AboutBanner;
