import React from "react";
import "./AboutPartners.css";

const partners = [
  { name: "TravelCo", logo: "/assets/images/partner1.png" },
  { name: "HotelX", logo: "/assets/images/partner2.png" }
];

const AboutPartners = () => (
  <section className="about-partners py-5 bg-light">
    <div className="container">
      <h2 className="text-center fw-bold mb-4">Đối tác của chúng tôi</h2>
      <div className="row justify-content-center g-4">
        {partners.map((p, idx) => (
          <div className="col-auto" key={idx}>
            <img src={p.logo} alt={p.name} className="partner-logo" height={60} />
          </div>
        ))}
      </div>
    </div>
  </section>
);

export default AboutPartners;
