import React from "react";
import '../../../styles/About/AboutFeatures.css'; 

const features = [
  {
    icon: "bi bi-geo-alt-fill",
    title: "Địa điểm đa dạng",
    desc: "Khám phá hàng chục điểm đến nổi bật, từ biển, núi đến di tích lịch sử."
  },
  {
    icon: "bi bi-calendar-check",
    title: "Lập kế hoạch dễ dàng",
    desc: "Công cụ lập lịch trình thông minh, cá nhân hóa chuyến đi của bạn."
  },
  {
    icon: "bi bi-people",
    title: "Cộng đồng năng động",
    desc: "Chia sẻ trải nghiệm, đánh giá và kết nối với các du khách khác."
  },
  {
    icon: "bi bi-shield-check",
    title: "An toàn & Tin cậy",
    desc: "Cam kết bảo mật thông tin và hỗ trợ khách hàng 24/7."
  }
];

const AboutFeatures = () => (
  <section className="about-features py-5 bg-light">
    <div className="container">
      <h2 className="text-center fw-bold mb-5">Điểm nổi bật</h2>
      <div className="row g-4">
        {features.map((f, idx) => (
          <div className="col-md-3" key={idx}>
            <div className="feature-card text-center p-4 h-100 shadow-sm bg-white rounded">
              <i className={`${f.icon} fs-1 text-primary mb-3`}></i>
              <h5 className="fw-bold mb-2">{f.title}</h5>
              <p className="mb-0">{f.desc}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  </section>
);

export default AboutFeatures;
